    <?php         class Kolibrii_Banner_Block_Adminhtml_Banner_Edit extends Mage_Adminhtml_Block_Widget_Form_Container    {        public function __construct()        {            parent::__construct();                               $this->_objectId = 'id';            $this->_blockGroup = 'banner';            $this->_controller = 'adminhtml_banner';                 $this->_updateButton('save', 'label', Mage::helper('banner')->__('Save Item'));            $this->_updateButton('delete', 'label', Mage::helper('banner')->__('Delete Item'));        }             public function getHeaderText()        {            if( Mage::registry('banner_data') && Mage::registry('banner_data')->getId() ) {                return Mage::helper('banner')->__("Edit Item '%s'", $this->htmlEscape(Mage::registry('banner_data')->getTitle()));            } else {                return Mage::helper('banner')->__('Add Item');            }        }    }