    <?php         class Kolibrii_Banner_Block_Adminhtml_Banner_Grid extends Mage_Adminhtml_Block_Widget_Grid    {        public function __construct()        {            parent::__construct();            $this->setId('bannerGrid');            // This is the primary key of the database            $this->setDefaultSort('banner_id');            $this->setDefaultDir('ASC');            $this->setSaveParametersInSession(true);            $this->setUseAjax(true);        }             protected function _prepareCollection()        {            $collection = Mage::getModel('banner/banner')->getCollection();            $this->setCollection($collection);            return parent::_prepareCollection();        }             protected function _prepareColumns()        {            $this->addColumn('banner_id', array(                'header'    => Mage::helper('banner')->__('ID'),                'align'     =>'right',                'width'     => '50px',                'index'     => 'banner_id',            ));                 $this->addColumn('title', array(                'header'    => Mage::helper('banner')->__('Title'),                'align'     =>'left',                'index'     => 'title',            ));						$this->addColumn('link', array(                'header'    => Mage::helper('banner')->__('link'),                'align'     =>'left',                'index'     => 'link',            ));						$this->addColumn('images', array(                'header'    => Mage::helper('banner')->__('images'),                'align'     =>'left',                'index'     => 'images',            ));                 $this->addColumn('created_time', array(                'header'    => Mage::helper('banner')->__('Creation Time'),                'align'     => 'left',                'width'     => '120px',                'type'      => 'date',                'default'   => '--',                'index'     => 'created_time',            ));                 $this->addColumn('update_time', array(                'header'    => Mage::helper('banner')->__('Update Time'),                'align'     => 'left',                'width'     => '120px',                'type'      => 'date',                'default'   => '--',                'index'     => 'update_time',            ));               return parent::_prepareColumns();        }             public function getRowUrl($row)        {            return $this->getUrl('*/*/edit', array('id' => $row->getId()));        }             public function getGridUrl()        {          return $this->getUrl('*/*/grid', array('_current'=>true));        }              }